INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139946, 'Glycerol phenylbutyrate', 'Midazolam', 'Moderate', 'Coadministration with glycerol phenylbutyrate may decrease the systemic exposure to and therapeutic efficacy of drugs that are substrates of CYP450 3A4. Glycerol phenylbutyrate, its active moiety phenylbutyrate, and active metabolite phenylacetic acid are considered weak CYP450 3A4 inducers in vivo.', NULL, 'Concomitant use of glycerol phenylbutyrate with sensitive CYP450 3A4 substrates (e.', NULL, 'Concomitant use of glycerol phenylbutyrate with sensitive CYP450 3A4 substrates (e.g., oral midazolam) or CYP450 3A4 substrates with a narrow therapeutic index (e.g., alfentanil, oral contraceptives, quinidine, and cyclosporine) should be done with caution and monitoring for reduced efficacy. Dosage adjustment as well as clinical and laboratory monitoring may be appropriate whenever glycerol phenylbutyrate is added to or withdrawn from therapy.', 'Metabolism', 'Suvorexant, Triazolam, Flurazepam, Methohexital, Methylphenobarbital, Pentobarbital, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Butabarbital, More', 'Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, Givosiran, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140107/', '[1] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (139947, 'Golimumab', 'Midazolam', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Suvorexant, Flurazepam, Methohexital, Zolpidem, Pentobarbital, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Butabarbital, Secobarbital, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, Abatacept, Upadacitinib, Siponimod, Apremilast, Inebilizumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/140108/', '[1] "Product Information. Skyrizi (risankizumab)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Gamifant (emapalumab)." Sobi Inc, Ardmore, PA.[5] "Product Information. Kevzara (sarilumab)." sanofi-aventis, Bridgewater, NJ.[6] "Product Information. Simponi (golimumab)." Centocor Inc, Malvern, PA.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc., Horsham, PA, PA.[9] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.[10] "Product Information. Ilumya (tildrakizumab)." Merck & Company Inc, Whitehouse Station, NJ.[11] "Product Information. Actemra (tocilizumab)." Genentech, South San Francisco, CA.[12] "Product Information. Amevive (alefacept)." Biogen, Cambridge, MA.[13] "Product Information. Remicade (infliximab)." Centocor Inc, Malvern, PA.[14] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company, Indianapolis, IN.[15] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[16] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[17] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of