INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (70618, 'Clomipramine', 'Lofexidine', 'Moderate', 'The concomitant administration of tricyclic antidepressants may theoretically reduce the effects of lofexidine and increase central nervous system depressant effects. In addition, lofexidine has been associated with asymptomatic prolongation of the QT interval in some patients. Coadministration with other agents that could prolong the QT interval, such as tricyclic antidepressants, may theoretically result in an elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', NULL, 'During concomitant use of these drugs, patients should be monitored for altered efficacy and excessive or prolonged CNS depression.', NULL, 'During concomitant use of these drugs, patients should be monitored for altered efficacy and excessive or prolonged CNS depression. Patients should be made aware of the possibility of additive effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and be cautioned about driving, operating machinery, or performing other hazardous tasks. The manufacturer recommends that the concurrent use of lofexidine with other medications that can prolong the QT interval be avoided. If no alternatives are available, patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/70705/', '[1] "Product Information. Lucemyra (lofexidine