INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (70806, 'Clomipramine', 'Saquinavir', 'Major', 'Saquinavir in combination with ritonavir may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of ritonavir-boosted saquinavir with other drugs that can prolong the QT interval is considered contraindicated.', NULL, 'Coadministration of ritonavir-boosted saquinavir with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/70893/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] FDA. U.S. Food and Drug Administration "FDA drug safety communication: Ongoing safety review of Invirase (saquinavir) and possible association with abnormal heart rhythms. Available from: URL: http://www.fda.gov/Drugs/DrugSafety/PostmarketDrugSafetyInformationforPatientsandProviders/ucm201221.h" ([2010 Feb 23]):[5] Biondi L "Health Canada endorsed important safety information on Invirase (saquinavir mesylate). Available from: URL: http://hc-sc.gc.ca/dhp-mps/alt_formats/pdf/medeff/advisories-avis/prof/2010/invirase_hpc-cps-eng.pdf." ([2010 Apr 14]):[6] Anson BD, Weaver JG, Ackerman MJ, et al. "Blockade of HERG channels by HIV protease inhibitors." Lancet 365 (2005): 682-686[7] Canadian Pharmacists Associat