INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (70950, 'Deferasirox', 'Clonazepam', 'Moderate', 'Coadministration with deferasirox may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism may involve induction of CYP450 3A4 activity by deferasirox, although in vitro, the drug has been shown to inhibit CYP450 3A4.', NULL, 'Caution is advised if deferasirox must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if deferasirox must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever deferasirox is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71037/', '[1] Skerjanec A, Wang J, Maren K, Rojkjaer L "Investigation of the pharmacokinetic interactions of deferasirox, a once-daily oral iron chelator, with midazolam, rifampin, and repaglinide in healthy volunteers." J Clin Pharmacol 50 (2010): 205-13[2] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (70951, 'Deserpidine', 'Clonazepam', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71038/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998): 595[7] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001): 435-40[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[10] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767365205);
