INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (70953, 'Desipramine', 'Clonazepam', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking clonazepam and tricyclic antidepressants.', NULL, 'Patients should be monitored for excessive or prolonged CNS and respiratory depression.', NULL, 'Patients should be monitored for excessive or prolonged CNS and respiratory depression. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, or confusion) and counseled to avoid activities requiring alertness until they know how these agents affect them. Patients should also be advised to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71040/', '[1] Deicken RF "Clonazepam-induced reduction in serum desipramine concentration." J Clin Psychopharmacol 8 (1988): 71-3', 1767365205);
