        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1366352, 'Betaxolol', 'Zolmitriptan', 'Minor', 'After one week of dosing with zolmitriptan and propranolol 160 mg/day, the Cmax and AUC of zolmitriptan increased 1.5 times while the Cmax and AUC of the N-desmethyl metabolite decreased by 30% and 15%, respectively. The mechanism of the interaction has not been described. It does not appear that any special precautions are necessary when coadministering zolmitriptan with beta-blockers, although further post-marketing studies will help to define this interaction.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
