INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (71125, 'Clonazepam', 'Pentazocine', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'Naloxone', 'Lacosamide', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71212/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (71126, 'Clonazepam', 'Pentobarbital', 'Moderate', 'Barbiturates may increase the plasma clearance of clonazepam and thereby reduce its clinical effectiveness. Phenobarbital is the only barbiturate specifically implicated in this interaction. However, other barbiturates may behave in a similar fashion. Also, clonazepam may increase the effectiveness of barbiturates. A case of respiratory depression and coma has been reported in a patient receiving high doses of both clonazepam and amobarbital.', NULL, 'If these drugs are used together, close observation for altered clonazepam and barbiturate effects is indicated.', NULL, 'If these drugs are used together, close observation for altered clonazepam and barbiturate effects is indicated.', 'Metabolism, Synergism', 'Lacosamide', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71213/', '[1] Khoo KC, Mendels J, Rothbart M, et al. "Influence of phenytoin and phenobarbital on the disposition of a single oral dose of clonazepam." Clin Pharmacol Ther 28 (1980): 368-75[2] Honer WG, Rosenberg RG, Turey M, Fisher WA "Respiratory failure after clonazepam and amobarbital ." Am J Psychiatry 143 (1986): 1495[3] Nanda RN, Johnson RH, Keogh HJ, Lambie DG, Melville ID "Treatment of epilepsy with clonazepam and its effect on other anticonvulsants." J Neurol Neurosurg Psychiatry 40 (1977): 538-43', 1767369485);
