INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (71178, 'Clonazepam', 'Saquinavir', 'Moderate', 'Coadministration with saquinavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by saquinavir.', NULL, 'Caution is advised if saquinavir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if saquinavir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever saquinavir is added to or withdrawn from therapy.', 'Metabolism', 'Ethotoin, Brivaracetam, Rufinamide, Valproic acid, Topiramate, Paramethadione, Mephenytoin, Methsuximide, Fenfluramine, Phensuximide, Lamotrigine, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71265/', '[1] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Fortovase (saquinavir)" Roche Laboratories, Nutley, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Kupferschmidt HHT, Fattinger KE, Ha HR, Follath F,