INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (71281, 'Desipramine', 'Clonidine', 'Major', 'Potentially life-threatening elevations in blood pressure have been observed when tricyclic antidepressants (TCAs) and clonidine have been administered concomitantly. The mechanism is postulated to be enhancement of the pressor response by the TCA. Reports are available for clomipramine, desipramine and imipramine.', NULL, 'It is recommended that this combination be avoided.', NULL, 'It is recommended that this combination be avoided. If no alternatives exist and this combination must be used, blood pressure should be monitored closely, especially during the first few weeks of therapy. When clonidine, the TCA, or the combination is discontinued, gradual withdrawal is recommended to prevent abrupt blood pressure fluctuations. Patients should be advised to seek medical help if they develop potential symptoms of a hypertensive crisis such as nausea, vomiting, sweating, flushing, dizziness, stiff neck, headache or palpitations.', 'Synergism', 'Latanoprost, Methazolamide, Neostigmine, Pilocarpine, Diclofenamide, Bimatoprost, Travoprost, Brinzolamide, Brimonidine, Timolol, Levobunolol, More', 'Oxitriptan, Viloxazine, Vortioxetine, Tryptophan, Vilazodone, Escitalopram', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/71368/', '[1] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[2] Stiff JL, Harris DB "Clonidine withdrawal complicated by amitriptyline therapy." Anesthesiology 59 (1983): 73-4[3] Elliott HL, McLean K, Sumner DJ, Reid JL "Absence of an effect of mianseri