INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366712, 'Dolutegravir', 'Bexarotene', 'Minor', 'Coadministration with inducers of UGT1A and CYP450 3A4 isoenzymes may decrease the plasma concentrations of dolutegravir, which is primarily metabolized by UGT1A1 with some contribution from CYP450 3A4. Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution may be advisable if dolutegravir is used in combination with mild or moderate UGT1A/CYP450 3A4 inducers.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
