INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366899, 'Bexarotene', 'Peginterferon beta-1a', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', 'DDInter', 1767317475, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366900, 'Bexarotene', 'Pemigatinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of pemigatinib, which is primarily metabolized by the isoenzyme in vitro.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of pemigatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of pemigatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
