INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366912, 'Bexarotene', 'Porfimer sodium', 'Moderate', 'Concomitant use of porfimer with drugs known to cause photosensitivity may increase the risk of a photosensitivity reaction. Photosensitivity with porfimer is due to residual drug being present in the skin. Patients treated with porfimer will be photosensitive for at least 30 days.', 'DDInter', 1767317475, 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', 'Synergism', 'Concomitant use of photosensitizing agents with porfimer should be avoided for at least 30 days and maybe up to 90 days or longer depending on patient''s hepatic and/or renal function.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366913, 'Bexarotene', 'Posaconazole', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may theoretically increase the plasma concentrations of bexarotene. In vitro studies suggest that bexarotene is metabolized by CYP450 3A4. However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo. Conversely, bexarotene appears to be an inducer of CYP450 3A4 and may reduce the plasma concentrations of CYP450 3A4 inhibitors that are also substrates of the isoenzyme, such as azole antifungals, macrolides, some calcium channel blockers, mifepristone, and nefazodone.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366914, 'Bexarotene', 'Praziquantel', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of praziquantel, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of praziquantel with CYP450 3A4 inducers should generally be avoided, since therapeutically effective blood levels of praziquantel may not be achieved. Alternative agents for schistosomiasis should be considered whenever possible in patients receiving treatment with a CYP450 3A4 inducer. Otherwise, clinical response to praziquantel should be closely monitored.', 'Metabolism', 'Concomitant use of praziquantel with CYP450 3A4 inducers should generally be avoided, since therapeutically effective blood levels of praziquantel may not be achieved.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366915, 'Bexarotene', 'Prednisolone', 'Moderate', 'Coadministration with bexarotene may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The proposed mechanism is increased clearance due to induction of CYP450 3A4 activity by bexarotene.', 'DDInter', 1767317475, 'Caution is advised if bexarotene is prescribed concomitantly with medications that are primarily metabolized by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever bexarotene is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if bexarotene is prescribed concomitantly with medications that are primarily metabolized by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
