INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366974, 'Bexarotene', 'St. John''s Wort', 'Moderate', 'Coadministration with St. John''s wort may alter the pharmacokinetics of antineoplastic agents that are substrates of the CYP450 3A4 isoenzyme (e.g., abiraterone, bexarotene, bortezomib, busulfan, cyclophosphamide, dasatinib, docetaxel, doxorubicin, etoposide, exemestane, ifosfamide, imatinib, irinotecan, letrozole, paclitaxel, sorafenib, sunitinib, tamoxifen, temsirolimus, teniposide, thiotepa, toremifene, tretinoin, vandetanib, vinca alkaloids). The mechanism is induction of CYP450 3A4 metabolism by constituents of St. John''s wort, which is expected to reduce the systemic levels and pharmacologic effects of many of these agents. In contrast, some agents such as cyclophosphamide and ifosfamide are prodrugs that are converted to active metabolites by CYP450 3A4, thus pharmacologic effects may be enhanced by St. John''s wort.', 'DDInter', 1767317475, 'Until more information is available, the use of St. John''s wort should probably be avoided before and during the entire course of chemotherapy. It is not known how much time should elapse between the discontinuation of St. John''s wort and initiation of chemotherapy. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Until more information is available, the use of St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366975, 'Bexarotene', 'Stavudine', 'Major', 'Coadministration with other drugs that are known to increase triglyceride levels or cause pancreatic toxicity may potentiate the risk of pancreatitis associated with the use of bexarotene.', 'DDInter', 1767317475, 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity. Other risk factors such as a history of pancreatitis, uncontrolled hyperlipidemia, excessive alcohol consumption, uncontrolled diabetes mellitus, or biliary tract disease should also be considered when weighing benefits versus risks of bexarotene therapy. All patients treated with bexarotene should have fasting blood lipids measured before initiating therapy, weekly during therapy until the lipid response to bexarotene is established (usually within two to four weeks), and at 8-week intervals thereafter.', 'Synergism', 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366976, 'Bexarotene', 'Stiripentol', 'Moderate', 'The concomitant administration of stiripentol may significantly increase plasma concentrations, pharmacologic effects, and toxicities of drugs that are substrates of CYP450 2C19, 3A4, and/or 2D6. The mechanism is inhibition of cytochrome P-450 metabolism by stiripentol.', 'DDInter', 1767317475, 'Clinical and laboratory monitoring for altered efficacy and safety is recommended during concomitant use. Dosage adjustments may be required if increased plasma levels or effects are observed.', 'Metabolism', 'Clinical and laboratory monitoring for altered efficacy and safety is recommended during concomitant use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366977, 'Bexarotene', 'Strontium chloride Sr-89', 'Moderate', 'The concomitant use of bone marrow depressants and strontium-89 chloride may have additive myelosuppressive effects.', 'DDInter', 1767317475, 'Patients should be monitored for excessive bone marrow suppression during treatment with strontium-89 chloride. Dose reductions of the other bone marrow depressants may be necessary. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', 'Patients should be monitored for excessive bone marrow suppression during treatment with strontium-89 chloride.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366978, 'Bexarotene', 'Sufentanil', 'Moderate', 'Coadministration with bexarotene may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The proposed mechanism is increased clearance due to induction of CYP450 3A4 activity by bexarotene.', 'DDInter', 1767317475, 'Caution is advised if bexarotene is prescribed concomitantly with medications that are primarily metabolized by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever bexarotene is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if bexarotene is prescribed concomitantly with medications that are primarily metabolized by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1366979, 'Bexarotene', 'Sulfinpyrazone', 'Minor', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bexarotene. In vitro studies suggest that bexarotene is metabolized by CYP450 3A4. However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo. The effects of concomitant CYP450 3A4 inducers (e.g., carbamazepine, phenytoin, phenobarbital, rifampin) on the pharmacokinetics of bexarotene have not been studied.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
