INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367012, 'Bexarotene', 'Trichophyton mentagrophytes', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367013, 'Bexarotene', 'Troglitazone', 'Minor', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of bexarotene. In vitro studies suggest that bexarotene is metabolized by CYP450 3A4. However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo. The effects of concomitant CYP450 3A4 inducers (e.g., carbamazepine, phenytoin, phenobarbital, rifampin) on the pharmacokinetics of bexarotene have not been studied.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367014, 'Bexarotene', 'Troleandomycin', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may theoretically increase the plasma concentrations of bexarotene. In vitro studies suggest that bexarotene is metabolized by CYP450 3A4. However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo. Conversely, bexarotene appears to be an inducer of CYP450 3A4 and may reduce the plasma concentrations of CYP450 3A4 inhibitors that are also substrates of the isoenzyme, such as azole antifungals, macrolides, some calcium channel blockers, mifepristone, and nefazodone.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
