INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367035, 'Bexarotene', 'Vitamin A', 'Moderate', 'Bexarotene is structurally related to vitamin A and may theoretically share some of the same toxicities. However, unlike vitamin A and other retinoids that bind retinoic acid receptors, bexarotene selectively activates retinoid X receptors, which can produce biologic activity distinct from the others.', 'DDInter', 1767317475, 'Patients should be advised to limit vitamin A supplements to 15,000 IU/day to avoid potential additive toxic effects.', 'Others', 'Patients should be advised to limit vitamin A supplements to 15,000 IU/day to avoid potential additive toxic effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367036, 'Bexarotene', 'Voriconazole', 'Major', 'Coadministration with other drugs that are known to increase triglyceride levels or cause pancreatic toxicity may potentiate the risk of pancreatitis associated with the use of bexarotene.', 'DDInter', 1767317475, 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity. Other risk factors such as a history of pancreatitis, uncontrolled hyperlipidemia, excessive alcohol consumption, uncontrolled diabetes mellitus, or biliary tract disease should also be considered when weighing benefits versus risks of bexarotene therapy. All patients treated with bexarotene should have fasting blood lipids measured before initiating therapy, weekly during therapy until the lipid response to bexarotene is established (usually within two to four weeks), and at 8-week intervals thereafter.', 'Synergism', 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367037, 'Bexarotene', 'Vortioxetine', 'Moderate', 'Coadministration with potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of vortioxetine, which is primarily metabolized by CYP450 2D6.', 'DDInter', 1767317475, 'An increase in the dosage of vortioxetine should be considered when used in combination with potent CYP450 inducers (e.g., carbamazepine, phenobarbital, phenytoin, rifamycins) for greater than 14 days, up to a maximum of three times the original dosage depending on clinical response. Following discontinuation of the potent CYP450 inducer, vortioxetine dosage should be returned to the original level within 14 days. Other known CYP450 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, enzalutamide, efavirenz, etravirine, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with vortioxetine is unknown.', 'Metabolism', 'An increase in the dosage of vortioxetine should be considered when used in combination with potent CYP450 inducers (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
