INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367078, 'Cisapride', 'Bicalutamide', 'Major', 'Bicalutamide inhibits the CYP450 3A4 enzyme in vitro and may theoretically interfere with the metabolism of cisapride and terfenadine. The accumulation of cisapride or terfenadine may prolong the QT interval and possibly may lead to the development of ventricular arrhythmias, including torsades de pointes. Clinical data have not been reported.', 'DDInter', 1767317475, 'Concomitant administration should generally by avoided.', 'Metabolism', 'Concomitant administration should generally by avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367079, 'Citalopram', 'Bicalutamide', 'Major', 'Citalopram can cause dose-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking citalopram with certain other drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317475, 'The use of citalopram is not recommended in patients receiving other drugs that prolong the QT interval. Citalopram is also not recommended in patients with congenital long QT syndrome, bradycardia, hypokalemia, hypomagnesemia, recent acute myocardial infarction, or uncompensated heart failure.', 'Synergism', 'The use of citalopram is not recommended in patients receiving other drugs that prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
