INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367124, 'Fesoterodine', 'Bicalutamide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of fesoterodine should be considered. Because 5-hydroxymethyl tolterodine is also metabolized by CYP450 2D6, the clinical significance of the interaction may be greater in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 (approximately 7% of Caucasians and less than 2% of Asians and individuals of African descent) who may rely more on the 3A4 metabolic pathway for clearance of the drug.', 'DDInter', 1767317475, 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary. Patients should be advised to notify their physician if they experience potential adverse effects of fesoterodine such as irregular heartbeat, blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, gastrointestinal upset, or constipation.', 'Metabolism', 'Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from fesoterodine therapy, and the dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367125, 'Fingolimod', 'Bicalutamide', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', 'DDInter', 1767317475, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
