INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367188, 'Bicalutamide', 'Maraviroc', 'Moderate', 'Coadministration with inhibitors of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may increase the plasma concentrations of maraviroc, which is a substrate of both.', 'DDInter', 1767317475, 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein. Dosage adjustment for maraviroc may be necessary if an interaction is suspected.', 'Metabolism', 'Caution is advised when maraviroc is used with inhibitors of CYP450 3A4 and/or P-glycoprotein.', NULL, NULL, 'Moderate', 'pharmacodynamic');
