INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72438, 'Clozapine', 'Phenytoin', 'Moderate', 'Several case reports suggest that phenytoin may reduce steady-state plasma concentrations of clozapine. Worsening of clinical status has been reported. Data exist for phenytoin, but this interaction may also occur with other hydantoins. The mechanism is unknown, but may be induction of CYP450 hepatic metabolism by phenytoin.', NULL, 'If a hydantoin and clozapine must be used together, close observation for clinical and laboratory evidence of altered clozapine effect is recommended, especially when initiating, changing dosage, or discontinuing the hydantoin.', NULL, 'If a hydantoin and clozapine must be used together, close observation for clinical and laboratory evidence of altered clozapine effect is recommended, especially when initiating, changing dosage, or discontinuing the hydantoin.', 'Metabolism', 'Lacosamide', 'Amisulpride', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72525/', '[1] Miller DD "Effect of phenytoin on plasma clozapine concentrations in two patients." J Clin Psychiatry 52 (1991): 23-5[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Dilantin (phenytoin)." Parke-Davis, Morris Plains, NJ.[6] Doak KK, Haas CE, Dunnigan KJ, et al. "Bioavailability of phenytoin acid and phenytoin sodium with enteral feedings." Pharmacotherapy 18 (1998): 637-45[7] Haley CJ, Nelson J "Phenytoin-enteral feeding interaction." DICP 23 (1989): 796-8[8] 