INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72443, 'Clozapine', 'Pimavanserin', 'Major', 'Clozapine has the potential to prolong QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can cause QT prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clozapine treatment alone has been associated with ventricular arrhythmia, torsade de pointes, cardiac arrest, and sudden death.', NULL, 'Caution is recommended if clozapine is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if clozapine is used in combination with other drugs that can prolong the QT interval. Serum electrolytes, including potassium, magnesium and calcium, should be measured at baseline and periodically during treatment, and any abnormalities corrected prior to initiating clozapine.', 'Synergism', 'Pimavanserin, Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Clozapine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72530/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[7] Penzak SR, Grimsley SR, Jann MW "Significant interactions with new antiretrovirals and psychotropic drugs." Ann Pharmacother 33 (1999): 1372-3[8] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[9] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[12] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[13] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72444, 'Clozapine', 'Pimozide', 'Major', 'Pimozide can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with pimozide. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', NULL, 'Coadministration of pimozide with other drugs that can prolong the QT interval is considered contraindicated.', NULL, 'Coadministration of pimozide with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Pimozide', 'Clozapine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72531/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orap Tablets (pimozide)." Gate Pharmaceuticals, Sellersville, PA.[3] Glassman AH, Bigger JT Jr "Antipsychotic drugs: prolonged QTc interval, torsade de pointes, and sudden death." Am J Psychiatry 158 (2001): 1774-82[4] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/docum