INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72465, 'Clozapine', 'Procainamide', 'Major', 'The use of clozapine with other potentially myelotoxic agents including procainamide may increase the risk and/or severity of hematologic toxicity. Clozapine alone is associated with a significant risk of agranulocytosis, defined as an absolute neutrophil count (ANC) of less than 500/mm3. Like other Class IA antiarrhythmic agents, procainamide can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval such as clozapine may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'The use of clozapine in combination with other agents having a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function is considered contraindicated.', NULL, 'The use of clozapine in combination with other agents having a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function is considered contraindicated. The concurrent use of procainamide with other medications that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', 'Synergism', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Moricizine, Lidocaine, Bretylium, Mexiletine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72552/', '[1] "Product Information. Procan SR (procainamide)." Parke-Davis, Morris Plains, NJ.[2] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[3] Trujillo TC, Nolan 