INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72490, 'Clozapine', 'Remifentanil', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'Amisulpride', 'Isoflurane, Nitrous oxide, Desflurane, Enflurane, Etomidate', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72577/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72491, 'Clozapine', 'Remimazolam', 'Major', 'Benzodiazepines and clozapine may have additive effects on respiratory and cardiovascular function. Ataxia, excessive salivation, hypotension, collapse, respiratory depression, respiratory arrest, loss of consciousness, cardiac arrest, and sudden death have been reported. The mechanism is unknown and causality has not been clearly determined.', NULL, 'Caution is advised if concurrent therapy is necessary.', NULL, 'Caution is advised if concurrent therapy is necessary. Vital signs should be closely monitored.', 'Synergism', 'Pimavanserin', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72578/', '[1] Klimke A, Klieser E "Sudden death after intravenous application of lorazepam in a patient treated with clozapine." Am J Psychiatry 151 (1994): 780[2] Grohmann R, Ruther E, Sassim N, Schmidt LG "Adverse effects of clozapine." Psychopharmacology (Berl) 99 (1989): s101-4[3] Cobb CD, Anderson CB, Seidel DR "Possible interaction between clozapine and lorazepam." Am J Psychiatry 148 (1991): 1606-7[4] Kupferschmidt HHT, Ha HR, Ziegler WH, Meier PJ, Krahenbuhl S "Interaction between grapefruit juice and midazolam in humans." Clin Pharmacol Ther 58 (1995): 20-8[5] Hukkinen SK, Varhe A, Olkkola KT, Neuvonen PJ "Plasma concentrations of triazolam are increased by concomitant ingestion of grapefruit juice." Clin Pharmacol Ther 58 (1995): 127-31[6] "Product Information. Valium (diazepam)." Roche Laboratories, Nutley, NJ.[7] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[8] "Product Information. Halcion (triazolam)." Pharmacia and Upjohn, Kalamazoo, M