INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72498, 'Clozapine', 'Rilpivirine', 'Major', 'Clozapine has the potential to prolong QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can cause QT prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clozapine treatment alone has been associated with ventricular arrhythmia, torsade de pointes, cardiac arrest, and sudden death.', NULL, 'Caution is recommended if clozapine is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if clozapine is used in combination with other drugs that can prolong the QT interval. Serum electrolytes, including potassium, magnesium and calcium, should be measured at baseline and periodically during treatment, and any abnormalities corrected prior to initiating clozapine.', 'Synergism', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72585/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[7] Penzak SR, Grimsley SR, Jann MW "Significant interactions with new antiretrovirals and psychotropic drugs." Ann Pharmacother 33 (1999): 1372-3[8] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[9] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[12] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[13] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72499, 'Clozapine', 'Botulinum toxin type B', 'Moderate', 'Use of anticholinergic drugs after administration of botulinum toxin may potentiate systemic anticholinergic effects such as dry mouth, blurred vision, and urinary disorders. Botulinum toxin inhibits the release of acetylcholine from peripheral cholinergic nerve endings, thus additive or synergistic anticholinergic effects may occur when these agents are used together.', NULL, 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.', NULL, 'Patients should be advised that systemic anticholinergic side effects such as dry mouth, blurred vision, and urinary disorders may increase if agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) are used after administration of botulinum toxin.', 'Synergism', 'Quetiapine, Asenapine, Pimavanserin, Ziprasidone, Lumateperone, Iloperidone, Risperidone, Amisulpride, Aripiprazole, Droperidol, Cariprazine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72586/', '[1] "Product Information. Botox (onabotulinumtoxin A)." Allergan Inc, Irvine, CA.[2] "Product Information. Myobloc (botulinum toxin type B)" Elan Pharmaceuticals, S. San Francisco, CA.[3] "Product Information. Xeomin (botulinum toxin type A (obsolete) (botulinum toxin type A))." Merz Pharmaceuticals, Greensboro, NC.[4] "Product Information. Jeuveau (prabotulinumtoxinA)." Evolus, Inc., Santa Barbara, CA.[5] "Product Information. Dysport (abobotulinumtoxinA)." Tercica Inc, Brisbane, CA.[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. Sanctura (trospium chloride)." Odyssey Pharmaceutical, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (72500, 'Clozapine', 'Risperidone', 'Major', 'Coadministration with other psychotropic agents may potentiate the adverse effects of clozapine on cardiovascular function. Orthostatic hypotension with or without syncope, in rare cases accompanied by profound collapse and cardiorespiratory arrest, has occurred during initiation of clozapine treatment alone and in combination with other psychotropic agents, occasionally even on the first dose. Anticholinergic effects of these agents may also be additively increased. Excessive anticholinergic effects may result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome.', NULL, 'Caution is advised when clozapine is initiated in patients receiving other psychotropic drugs, including risperidone.', NULL, 'Caution is advised when clozapine is initiated in patients receiving other psychotropic drugs, including risperidone. Vital signs should be closely monitored. Patients who have had even a brief interval off clozapine (i.e., 2 or more days since the last dose) should be restarted with 12.5 mg once or twice daily. Conversely, pharmacologic response and plasma clozapine levels should be monitored more closely whenever risperidone is added to or withdrawn from therapy in patients stabilized on clozapine, and the dosage adjusted as necessary. In addition, the potential for additive effects on the QT interval and increased risk of torsade de pointes arrhythmia should be considered. Serum electrolytes, including potassium, magnesium and calcium, should be measured at baseline and periodically during treatment, and any abnormalities corrected prior to initiating treatment. Routine ECG assessment may detect QTc prolongation but is not always effective in preventing arrhythmias. Therapy with these agents should be discontinued if the QTc interval exceeds 500 msec. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Ambulatory patients may want to avoid activities requiring mental alertness until they know how these agents affect them. A dosage reduction in one or both drugs may be necessary if excessive adverse effects develop.', 'Metabolism, Synergism', 'Risperidone', 'Clozapine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/72587/', '[1] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[2] Tyson SC, Devane CL, Risch SC "Pharmacokinetic interaction between risperidone and clozapine." Am J Psychiatry 152 (1995): 1401-2[3] Zelman S, Guillan R "Heat stroke in phenothiazine-treated patients: a report of three fatalities." Am J Psychiatry 126 (1970): 1787-90[4] Cohen MA, Alfonso CA, Mosquera M "Development of urinary retention during treatment with clozapine and meclizine [published erratum appears in Am J Psychiatry 1994 Jun;151(6):952]." Am J Psychiatry 151 (1994): 619-20[5] Warnes H, Lehmann HE, Ban TA "Adynamic ileus during psychoactive medication: a report of three fatal and five severe cases." Can Med Assoc J 96 (1967): 1112-3[6] Mann SC, Boger WP "Psychotropic drugs, summer heat and humidity, and hyperplexia: a danger restated." Am J Psychiatry 135 (1978): 1097-100[7] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[8] Sala M, Vicentini A, Brambilla P, et al. "QT interval prolongation related to psychoactive drug treatment: a comparison of monotherapy versus polytherapy." Ann Gen Psychiatry 4 (2005): 1[9] Johnson AL, Hollister LE, Berger PA "The anticholinergic intoxication syndrome: diagnosis and treatment." J Clin Psychiatry 42 (1981): 313-7[10] Moreau A, Jones BD, Banno V "Chronic central anticholinergic toxicity in manic depressive illness mimicking dementia." Can J Psychiatry 31 (1986): 339-41[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[12] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[13] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[14] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[15] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.', 1767369485);
