INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (144655, 'Gilteritinib', 'Niraparib', 'Moderate', 'Coadministration with gilteritinib may increase the plasma concentrations and the risk of adverse effects of orally administered drugs that are substrates of the intestinal P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic cation transporter 1 (OCT1) transporters, such as dabigatran, digoxin, rosuvastatin, methotrexate, and metformin.', NULL, 'Until more information is available, caution is advised if gilteritinib is used concomitantly with drugs that are substrates of the intestinal P-gp, BCRP, and/or OCT1 transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Until more information is available, caution is advised if gilteritinib is used concomitantly with drugs that are substrates of the intestinal P-gp, BCRP, and/or OCT1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever gilteritinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, More', 'Imatinib, Dabrafenib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Larotrectinib, Lapatinib, Selumetinib, Vandetanib, Afatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/144818/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Xospata (gilt