    ) VALUES (1367304, 'Bicalutamide', 'Terfenadine', 'Major', 'Bicalutamide inhibits the CYP450 3A4 enzyme in vitro and may theoretically interfere with the metabolism of cisapride and terfenadine. The accumulation of cisapride or terfenadine may prolong the QT interval and possibly may lead to the development of ventricular arrhythmias, including torsades de pointes. Clinical data have not been reported.', NULL, 'Concomitant administration should generally by avoided.', NULL, 'Concomitant administration should generally by avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
