    ) VALUES (1367340, 'Abiraterone', 'Bictegravir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of bictegravir.', NULL, 'The use of bictegravir with P-gp and/or BCRP inhibitors should generally be avoided.', NULL, 'The use of bictegravir with P-gp and/or BCRP inhibitors should generally be avoided. If concomitant use is required, caution and close clinical and laboratory monitoring, including renal function, are recommended. Dosage adjustments may be required whenever a P-gp and/or BCRP inhibitor is added to or withdrawn from therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
