        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1367504, 'Amprenavir', 'Binimetinib', 'Moderate', 'Coadministration with inducers of CYP450 1A2, P-glycoprotein (P-gp), and/or uridine diphosphate glucuronosyltransferase (UGT) 1A1 or 2B7 may decrease the plasma levels and therapeutic efficacy of binimetinib. The proposed mechanism involves enhanced metabolic clearance of binimetinib through induction of CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7 inducers.', NULL, 'Until further information is available, caution is recommended if binimetinib must be used concomitantly with CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7 inducers. Pharmacologic response to binimetinib should be monitored more closely whenever a CYP450 1A2, P-gp, and/or UGT 1A1 or 2B7 inducer is added to or withdrawn from therapy, and the binimetinib dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
