INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73002, 'Cobimetinib', 'Samarium (153Sm) lexidronam', 'Major', 'Theoretical concerns exist that chemotherapeutic agents and other bone marrow depressants may potentiate the myelosuppressive effects of samarium sm 153 lexidronam.', NULL, 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks.', NULL, 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks. Moreover, samarium sm 153 lexidronam should not be given after either of these treatments until there has been time for adequate marrow recovery. Caution and close monitoring of bone marrow function are advisable if coadministration with other myelotoxic agents is required. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, unusual bleeding or bruising, or signs of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73089/', '[1] "Product Information. Quadramet (samarium sm 153 lexidronam)" Berlex Laboratories, Richmond, CA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73003, 'Cobimetinib', 'Saquinavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of cobimetinib, which is primarily metabolized by the isoenzyme. The risk and/or severity of adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities, and rhabdomyolysis may be increased.', NULL, 'Concomitant use of cobimetinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of cobimetinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of cobimetinib during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73090/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365205);
