INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73033, 'Cobimetinib', 'Topotecan', 'Moderate', 'The use of topotecan with other cytotoxic agents is likely to result in greater myelosuppression due to additive or synergistic pharmacologic effects. Severe neutropenia and neutropenic sepsis resulting in death were reported with topotecan and cisplatin.', NULL, 'Caution is advised if topotecan is used with other cytotoxic agents.', NULL, 'Caution is advised if topotecan is used with other cytotoxic agents. Dosage reductions may be required, and bone marrow function should be closely monitored. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73120/', '[1] "Product Information. Hycamtin (topotecan)." SmithKline Beecham, Philadelphia, PA.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73034, 'Cobimetinib', 'Trabectedin', 'Moderate', 'The risk of rhabdomyolysis may be increased during coadministration of trabectedin with other agents that are also associated with this toxicity, such as colchicine, alectinib, cobimetinib, statins, fibric acid derivatives, and lipid-lowering doses of niacin. The risk of liver injury may be increased during coadministration of trabectedin with other potentially hepatotoxic agents, including alectinib, cobimetinib, statins, fibric acid derivatives, and lipid-lowering doses of niacin.', NULL, 'Caution is advised when trabectedin is used with other agents associated with rhabdomyolysis.', NULL, 'Caution is advised when trabectedin is used with other agents associated with rhabdomyolysis. Patients should be advised to seek medical attention if they experience unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Caution is advised when trabectedin is used with other agents that are potentially hepatotoxic. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73121/', '[1] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365205);
