INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73048, 'Cobimetinib', 'Verapamil', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of cobimetinib, which is primarily metabolized by the isoenzyme. The risk and/or severity of adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities, and rhabdomyolysis may be increased.', NULL, 'Concomitant use of cobimetinib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.', NULL, 'Concomitant use of cobimetinib with moderate CYP450 3A4 inhibitors should be avoided whenever possible. If short-term (14 days or less) use of a moderate CYP450 3A4 inhibitor is required during treatment with cobimetinib 60 mg once daily, the manufacturer suggests reducing the cobimetinib dose to 20 mg. Following discontinuation of the moderate CYP450 3A4 inhibitor, the previous dose of 60 mg may be resumed. An alternative to the moderate CYP450 3A4 inhibitor should be used in patients who are already taking a reduced dose of cobimetinib (40 or 20 mg daily).', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73135/', '[1] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.', 1767365205);
