INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73082, 'Iobenguane (I-123)', 'Cocaine (topical)', 'Moderate', 'Coadministration with drugs that block norepinephrine uptake or deplete norepinephrine stores may decrease iobenguane I-123 uptake in neuroendocrine tumors and lead to false-negative imaging results.', NULL, 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.', NULL, 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73169/', '[1] "Product Information. AdreView (iobenguane I-123)." GE Healthcare, Princeton, NJ.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73083, 'Iobenguane (I-131)', 'Cocaine (topical)', 'Major', 'Coadministration with drugs that reduce catecholamine uptake or deplete catecholamine stores may interfere with iobenguane I-131 uptake into neuroendocrine tumors such as pheochromocytoma and paraganglioma that express high levels of norepinephrine transporter on their cell surfaces. Since iobenguane is similar in structure to norepinephrine and is subject to the same uptake and accumulation pathways as norepinephrine, drugs that alter norepinephrine disposition in adrenergic nerve terminals and presynaptic storage vesicles will likewise affect iobenguane.', NULL, 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131.', NULL, 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131. Do not administer these drugs until at least 7 days after each iobenguane I-131 dose. Patients should be monitored for the occurrence of clinically significant withdrawal symptoms, especially patients with elevated levels of circulating catecholamines and their metabolites.', 'Distribution', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73170/', '[1] "Product Information. Azedra (iobenguane I-131)." Progenics Pharmaceuticals, Inc., New York, NY.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73084, 'Ioflupane I-123', 'Cocaine (topical)', 'Moderate', 'Drugs that bind to the dopamine transporter with high affinity may interfere with the image obtained using ioflupane I-123, which also binds to the dopamine transporter. These drugs include amoxapine, amphetamine, benztropine, bupropion, buspirone, cocaine, diethylpropion, ketamine, mazindol, methamphetamine, methylphenidate, modafinil, pemoline, phencyclidine (PCP), phentermine, phenylpropanolamine, pipradrol, selegiline, sertraline, and trihexyphenidyl. Selective serotonin reuptake inhibitors (SSRIs) and serotonin-norepinephrine reuptake inhibitors (SNRIs) may increase or decrease ioflupane binding to the dopamine transporter. Whether discontinuation of these drugs prior to ioflupane I-123 administration may help to minimize potential interference is unknown.', NULL, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', NULL, 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73171/', '[1] "Product Information. DaTscan (ioflupane I-123)." GE Healthcare, Princeton, NJ.[2] "Product Information. Viibryd (vilazodone)." Trovis Pharmaceuticals LLC, New Haven, CT.', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73085, 'Isocarboxazid', 'Cocaine (topical)', 'Major', 'Cocaine exerts an indirect sympathomimetic effect and may precipitate hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs), as has been reported with other indirect- or mixed-acting sympathomimetic agents such as amphetamine and ephedrine. The mechanism involves a synergistic effect due to enhanced norepinephrine storage in adrenergic neurons (MAOI activity) and decreased reuptake of catecholamines (indirect sympathomimetic activity).', NULL, 'In general, indirect-acting sympathomimetic agents such as cocaine should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, 'In general, indirect-acting sympathomimetic agents such as cocaine should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with sympathomimetic agents.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73172/', '[1] "Product Information. Cocaine Hydrochloride Nasal (cocaine nasal)." Genus Lifesciences Inc., Allentown, PA.[2] "Product Information. Nardil (phenelzine)." Parke-Dvis, Morris Plains, NJ.[3] Tordoff SG, Stubbing JF, Linter SP "Delayed excitatory reaction following interaction of cocaine and monoamine oxidase inhibitor (phenelzine)." Br J Anaesth 66 (1991): 516-8[4] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[5] Brewer C "Treatment of cocaine abuse with monoamine oxidase inhibitors." Br J Psychiatry 163 (1993): 815-6[6] Smith RB "Cocaine and catecholamine interaction. A review." Arch Otolaryngol 98 (1973): 139-41[7] "Product Information. Parnate (tranylcypromine)." SmithKline Beecham, Philadelphia, PA.[8] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[9] Ban TA "Drug interactions with psychoactive drugs." Dis Nerv Syst 36 (1975): 164-6[10] "Product Information. Zyvox (linezolid)" Pharmacia and Upjohn, Kalamazoo, MI.[11] Zetin M, Plon L, DeAntonio M "MAOI reaction with powdered protein dietary supplement." J Clin Psychiatry 48 (1987): 499[12] Maxwell MB "Reexamining the dietary restrictions with procarbazine (an MAOI)." Cancer Nurs 3 (1980): 451-7[13] Tailor SA, Shulman KI, Walker SE, Moss J, Gardner D "Hypertensive episode associated with phenelzine and tap beer--a reanalysis of the role of pressor amines in beer." J Clin Psychopharmacol 14 (1994): 5-14[14] Sweet RA, Liebowitz MR, Holt CS, Heimberg RG "Potential interactions between monoamine oxidase inhibitors and prescribed dietary supplements." J Clin Psychopharmacol 11 (1991): 331-2[15] Martin TG "Serotonin syndrome." Ann Emerg Med 28 (1996): 520-6[16] Darcy PF, Griffin JP "Interactions with drugs used in the treatment of depressive illness." Adverse Drug React Toxicol Rev 14 (1995): 211-31[17] Nuessle WF, Norman FC, Miller HE "Pickled herring and tranylcypromine reaction." JAMA 192 (1965): 142-3[18] "Product Information. Furoxone (furazolidone)." Roberts Pharmaceutical Corporation, Eatontown, NJ.[19] Goldberg LI "Monoamine oxidase inhibitors: adverse reactions and possible mechanisms." JAMA 190 (1964): 456-62[20] Pettinger WA, Soyangco FG, Oates JA "Inhibition of monoamine oxidase in man by furazolidone." Clin Pharmacol Ther 9 (1968): 442-7[21] Domino EF, Selden EM "Red wine and reactions." J Clin Psychopharmacol 4 (1984): 173-4[22] Walker JI, Davidson J, Zung WWK "Patient compliance with MAO Inhibitor therapy." J Clin Psychiatry