INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73464, 'Deferasirox', 'Colesevelam', 'Moderate', 'Coadministration with cholestyramine may decrease the bioavailability and efficacy of deferasirox. The mechanism involves binding of cholestyramine to deferasirox and its glucuronide metabolites, which are secreted into the bile. This interferes with the enterohepatic recycling of deferasirox by preventing intestinal deconjugation of the glucuronide metabolites and subsequent reabsorption. In healthy volunteers, administration of cholestyramine (12 grams twice daily) 4 and 10 hours after a single dose of deferasirox resulted in a 45% decrease in deferasirox systemic exposure (AUC). The interaction has not been studied with other bile acid sequestrants.', NULL, 'The concomitant use of deferasirox with cholestyramine should generally be avoided.', NULL, 'The concomitant use of deferasirox with cholestyramine should generally be avoided. Otherwise, consideration should be given to increasing the initial dosage of deferasirox by 50% with subsequent adjustments made according to serum ferritin levels and clinical response. It is not known whether other bile acid sequestrants interact similarly with deferasirox.', 'Metabolism, Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73551/', '[1] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365205);
