INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (146150, 'Ofloxacin', 'Levacetylmethadol', 'Major', 'Levomethadyl acetate may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. High dosages of levomethadyl acetate alone have been associated with QT interval prolongation and torsade de pointes.', NULL, 'Coadministration of levomethadyl acetate with other drugs that can prolong the QT interval is considered contraindicated.', NULL, 'Coadministration of levomethadyl acetate with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Enoxacin, Nalidixic acid, Lomefloxacin, Grepafloxacin, Cinoxacin, Trovafloxacin, Delafloxacin, Tinidazole, Tetracycline, Cefepime, Cefuroxime, More', 'Nalmefene, Disulfiram, Diamorphine, Naltrexone', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146313/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[3] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[4] Krantz MJ, Mehler PS "Synthetic opioids and QT prolongation." Arch Intern Med 163 (2003): 1615; author reply 1615[5] Deamer RL, Wilson DR, Clark DS, Prichard JG "Torsades de pointes associated with high dose levomethadyl acetate (ORLAAM)." J Addict Dis 20 (2001): 7-14[6] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (146151, 'Ofloxacin', 'Lindane', 'Moderate', 'Lindane penetrates human skin and has the potential to cause central nervous system toxicity. Seizures have been reported after excessive use or oral ingestion of lindane. There may be a theoretical risk of increased seizure potential when lindane is used with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), and/or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, theophylline).', NULL, 'Caution is advised if lindane is used with any substance that can reduce the seizure threshold, particularly in the very young or the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.', NULL, 'Caution is advised if lindane is used with any substance that can reduce the seizure threshold, particularly in the very young or the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.g., head trauma, brain tumor, metabolic disorders, alcohol and drug withdrawal, CNS infections). Lindane should be used according to recommended dosage and directions for application.', 'Synergism', 'Disulfiram', 'Tinidazole, Tetracycline, Cefepime, Fluconazole, Metronidazole, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Rifamycin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/146314