INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (146727, 'Terfenadine', 'Olaparib', 'Moderate', 'Based on in vitro inhibition data, coadministration with olaparib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4 (e.g., cisapride, ergot alkaloids, fentanyl, lovastatin, oral midazolam, pimozide, quetiapine, simvastatin, triazolam, vinca alkaloids), P-gp (e.g., colchicine, dabigatran, digoxin), breast cancer resistance protein (BCRP) (e.g., rosuvastatin), OATP1B1 (e.g., eluxadoline, glyburide, repaglinide, statins, valsartan), or organic cation transporter 1 or 2 (OCT1, OCT2) (e.g., metformin). The proposed mechanism is decreased clearance due to inhibition of the corresponding metabolizing enzyme and/or efflux/uptake transporter by olaparib.', NULL, 'Caution is advised if olaparib must be used concomitantly with drugs that are substrates of the affected enzymes or transporters, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if olaparib must be used concomitantly with drugs that are substrates of the affected enzymes or transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever olaparib is added to or withdrawn from therapy.', 'Metabolism', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Promethazine, More', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https:/