INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73520, 'Colestipol', 'Cerivastatin', 'Moderate', 'INTERVAL: When administered concomitantly, cholestyramine has been reported to decrease the rate and extent of fluvastatin bioavailability. Other bile acid sequestrants may also decrease the bioavailability of other HMG-CoA reductase inhibitors. The mechanism is most likely due to adsorption of the HMG-CoA reductase inhibitor to the bile acid sequestrant. Separating administration times has been shown to decrease the interaction.', NULL, 'If therapy with both agents is necessary, the HMG-CoA reductase inhibitor should be administered at least 4 hours after the bile acid sequestrant.', NULL, 'If therapy with both agents is necessary, the HMG-CoA reductase inhibitor should be administered at least 4 hours after the bile acid sequestrant.', 'Absorption', 'Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cerivastatin, Colesevelam, Alirocumab', 'Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Lovastatin, Cholestyramine, Rosuvastatin, Clofibrate, Colestipol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73607/', '[1] Smith HT, Jokubaitis LA, Troendle AJ, Hwang DS, Robinson WT "Pharmacokinetics of fluvastatin and specific drug interactions." Am J Hypertens 6 (1993): s375-82[2] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[3] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice-simvastatin interaction: Effect on serum concentrations of simvastatin, simvastatin acid, and HMG-CoA reductase inhibitors." Clin Pharmacol Ther 64 (1998): 477-83[4] Kantola T, Kivisto KT, Neuvonen PJ "Grapefruit juice greatly increases serum concentrations of lovastatin and lovastatin acid." Clin Pharmacol Ther 63 (1998): 397-402[5] "Product Information. Mevacor (lovastatin)." Merck & Co, Inc, West Point, PA.[6] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[7] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[8] Thompson PD, Clarkson P, Karas RH "Statin-associated myopathy." JAMA 289 (2003): 1681-90[9] "Product Information. Zocor (simvastatin)." Merck & Co, Inc, West Point, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73521, 'Deferasirox', 'Colestipol', 'Moderate', 'Coadministration with cholestyramine may decrease the bioavailability and efficacy of deferasirox. The mechanism involves binding of cholestyramine to deferasirox and its glucuronide metabolites, which are secreted into the bile. This interferes with the enterohepatic recycling of deferasirox by preventing intestinal deconjugation of the glucuronide metabolites and subsequent reabsorption. In healthy volunteers, administration of cholestyramine (12 grams twice daily) 4 and 10 hours after a single dose of deferasirox resulted in a 45% decrease in deferasirox systemic exposure (AUC). The interaction has not been studied with other bile acid sequestrants.', NULL, 'The concomitant use of deferasirox with cholestyramine should generally be avoided.', NULL, 'The concomitant use of deferasirox with cholestyramine should generally be avoided. Otherwise, consideration should be given to increasing the initial dosage of deferasirox by 50% with subsequent adjustments made according to serum ferritin levels and clinical response. It is not known whether other bile acid sequestrants interact similarly with deferasirox.', 'Metabolism, Absorption', 'Gemfibrozil, Fenofibric acid, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Ezetimibe, Rosuvastatin, Clofibrate, Niacin, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73608/', '[1] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73522, 'Deflazacort', 'Colestipol', 'Moderate', 'INTERVAL: Concurrent administration of bile acid sequestrants may delay and/or decrease the absorption of oral corticosteroids. Bile acid sequestrants can bind certain drugs in the intestine to form an insoluble complex that is excreted in the feces. Reduced bioavailability of hydrocortisone has been reported during concomitant administration with cholestyramine and colestipol. It is not known whether the interaction occurs with other bile acid binding resins such as colesevelam or with corticosteroids other than hydrocortisone, but the possibility should be considered.', NULL, 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', NULL, 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', 'Absorption', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Lomitapide, Ezetimibe, Niacin, More', 'Betamethasone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73609/', '[1] Johansson C, Adamsson U, Stierner U, Lindsten T "Interaction by cholestyramine on the uptake of hydrocortisone in the gastrointestinal tract." Acta Med Scand 204 (1978): 509-12[2] "Product Information. Colestid (colestipol HCI)." Pharmacia and Upjohn, Kalamazoo, MI.[3] Nekl KE, Aron DC "Hydrocortisone-colestipol interaction." Ann Pharmacother 27 (1993): 980-1[4] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[5] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.', 1767369485);
