INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73548, 'Colestipol', 'Methylprednisolone', 'Moderate', 'INTERVAL: Concurrent administration of bile acid sequestrants may delay and/or decrease the absorption of oral corticosteroids. Bile acid sequestrants can bind certain drugs in the intestine to form an insoluble complex that is excreted in the feces. Reduced bioavailability of hydrocortisone has been reported during concomitant administration with cholestyramine and colestipol. It is not known whether the interaction occurs with other bile acid binding resins such as colesevelam or with corticosteroids other than hydrocortisone, but the possibility should be considered.', NULL, 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', NULL, 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', 'Absorption', 'Gemfibrozil, Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Lovastatin, Clofibrate, Cerivastatin, More', 'Clindamycin, Isotretinoin, Chloramphenicol, Fluorometholone, Tretinoin, Erythromycin, Dapsone, Azelaic acid, Adapalene, Betamethasone, Fluocinolone acetonide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73635/', '[1] Johansson C, Adamsson U, Stierner U, Lindsten T "Interaction by cholestyramine on the uptake of hydrocortisone in the gastrointestinal tract." Acta Med Scand 204 (1978): 509-12[2] "Product Information. Colestid (colestipol HCI)." Pharmacia and Upjohn, Kalamazoo, MI.[3] Nekl KE, Aron DC "Hydrocortisone-colestipol interaction." Ann Pharmacother 27 (1993): 980-1[4] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[5] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.', 1767369485);
