INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73570, 'Colestipol', 'Tetracycline', 'Minor', 'Colestipol may decrease the absorption of tetracycline. The therapeutic response to tetracycline may be reduced. Absorption of other tetracyclines may be affected as well. Tetracyclines should be administered at least one hour before or four hours after colestipol.', NULL, '-', NULL, '-', 'Absorption', 'Gemfibrozil, Fenofibric acid, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Cerivastatin, Fenofibrate, Bempedoic acid, More', 'Rifamycin, Miconazole, Polymyxin B, Ciprofloxacin, Chloramphenicol, Gentamicin, Chlorhexidine, Neomycin, Ofloxacin, Tinidazole, Dexlansoprazole, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73657/', '[1] Friedman H, Greenblatt DJ, LeDuc BW "Impaired absorption of tetracycline by colestipol is not reversed by orange juice." J Clin Pharmacol 29 (1989): 748-51[2] Heel RC, Brogden RN, Pakes GE, Speight TM, Avery GS "Colestipol: a review of its pharmacological properties and therapeutic efficacy in patients with hypercholesterolaemia." Drugs 19 (1980): 161-80[3] "Product Information. Colestid (colestipol HCI)." Pharmacia and Upjohn, Kalamazoo, MI.[4] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[5] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73571, 'Colestipol', 'Thyroid, porcine', 'Moderate', 'INTERVAL: Bile acid sequestrants may bind to and decrease the gastrointestinal absorption of thyroid hormones. There have been isolated reports of worsening hypothyroidism and increased TSH levels following the addition of cholestyramine in patients receiving thyroid replacement therapy.', NULL, 'Thyroid hormone preparations and bile acid sequestrants should be administered at least 4 hours apart.', NULL, 'Thyroid hormone preparations and bile acid sequestrants should be administered at least 4 hours apart. Close clinical and laboratory monitoring of thyroid function is recommended during coadministration, since adjusting the dosing interval may not completely prevent the interaction.', 'Absorption', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Probucol, Cerivastatin, Lomitapide, Ezetimibe, Niacin, Atorvastatin, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73658/', '[1] Harmon SM, Seifert CF "Levothyroxine-cholestyramine interaction reemphasized." Ann Intern Med 115 (1991): 658-9[2] Cashin-Hemphill L, Spencer CA, Nicoloff JT, Blankenhorn DH, Nessim SA, Chin HP, Lee NA "Alterations in serum thyroid hormonal indices with colestipol-niacin therapy." Ann Intern Med 107 (1987): 324-9[3] Northcutt RC, Stiel JN, Hollifield JW, Stant EG "The influence of cholestyramine on thyroxine absorption." JAMA 208 (1969): 1857-61[4] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[5] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.[6] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[7] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[8] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73572, 'Colestipol', 'Torasemide', 'Moderate', 'INTERVAL: Limited data have shown that the bioavailability of orally administered furosemide is reduced by 95% during concurrent administration of cholestyramine and by 80% during concurrent administration of colestipol. (These figures are based on data from the area under the plasma concentration-time curve and total urinary elimination data.) These decreases in bioavailability were associated with 77% and 58% reductions in the diuretic response to furosemide. No data are available for bumetanide, torsemide, or ethacrynic acid, but a similar interaction is suspected.', NULL, 'Diuretics should be administered at least 2 to 3 hours before cholestyramine or colestipol.', NULL, 'Diuretics should be administered at least 2 to 3 hours before cholestyramine or colestipol. If alternating dosage schedules is ineffective, it may be necessary to increase diuretic dosage during coadministration. Patients should be monitored for altered diuretic efficacy.', 'Absorption', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, More', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Cerivastatin, Lomitapide, Bempedoic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73659/', '[1] "Product Information. Colestid (colestipol HCI)." Pharmacia and Upjohn, Kalamazoo, MI.[2] Neuvonen PJ, Kivisto K, Hirvisalo EL "Effects of resins and activated charcoal on the absorption of digoxin, carbamazepine and frusemide." Br J Clin Pharmacol 25 (1988): 229-33', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73573, 'Colestipol', 'Trichlormethiazide', 'Moderate', 'INTERVAL: Colestipol may decrease the gastrointestinal absorption of thiazide diuretics by binding them in the gut. The interaction has been studied with hydrochlorothiazide (HCTZ) and chlorothiazide.', NULL, 'Thiazide diuretics should be administered as far apart from colestipol as possible, but at least 1 hour before or 4 hours after colestipol.', NULL, 'Thiazide diuretics should be administered as far apart from colestipol as possible, but at least 1 hour before or 4 hours after colestipol. Patients should be monitored for potentially diminished therapeutic effects, and the thiazide dosage adjusted if necessary.', 'Absorption', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Lomitapide, Ezetimibe, Niacin, More', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73660/', '[1] Kauffman RE, Azarnoff DL "Effect of colestipol on gastrointestinal absorption of chlorothiazide in man." Clin Pharmacol Ther 14 (1973): 886-90[2] Hunninghake DB, King S, LaCroix K "The effect of cholestyramine and colestipol on the absorption of hydrochlorothiazide." Int J Clin Pharmacol Ther Toxicol 20 (1982): 151-4[3] "Product Information. Colestid (colestipol HCI)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73574, 'Colestipol', 'Venetoclax', 'Moderate', 'Coadministration of bile acid sequestrants may reduce the absorption of venetoclax, possibly reducing its pharmacologic effect. Bile acid sequestrants can bind certain drugs in the intestine to form an insoluble complex that is excreted in the feces.', NULL, 'If concomitant use is required, venetoclax should be administered at least 4 to 6 hours after the bile acid sequestrant.', NULL, 'If concomitant use is required, venetoclax should be administered at least 4 to 6 hours after the bile acid sequestrant. Close clinical and laboratory monitoring may be considered during coadministration, since adjusting the dosing interval may not completely prevent the interaction.', 'Absorption', 'Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Ezetimibe, Niacin, Atorvastatin, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73661/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.', 1767369485);
