INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73733, 'Dolasetron', 'Prochlorperazine', 'Major', 'Dolasetron can cause dose-related prolongation of the QT interval via its pharmacologically active metabolite, hydrodolasetron. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if dolasetron is used in combination with cumulative high-dose anthracycline therapy or other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if dolasetron is used in combination with cumulative high-dose anthracycline therapy or other drugs that can prolong the QT interval. ECG monitoring may be appropriate in high-risk patients. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole, Lurasidone', 'Rolapitant', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/73820/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Anzemet (dolasetron)." Hoechst Marion-Roussel Inc, Kansas City, MO.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish