INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (73981, 'Prochlorperazine', 'Paroxetine', 'Moderate', 'The coadministration with paroxetine may increase the plasma concentrations of phenothiazines. The mechanism is paroxetine inhibition of CYP450 2D6, the isoenzyme responsible for the metabolic clearance of most, if not all, phenothiazines. The interaction has been studied specifically with perphenazine.', NULL, 'Caution is advised if phenothiazines must be used concomitantly with paroxetine.', NULL, 'Caution is advised if phenothiazines must be used concomitantly with paroxetine. Pharmacologic response to the phenothiazine should be monitored more closely whenever paroxetine is added to or withdrawn from therapy, and the phenothiazine dosage adjusted as necessary. Patients should be monitored for the development of extrapyramidal symptoms (e.g., tremor, shuffling gait, drooling, mask-like face, tongue stiffness, muscle spasms or rigidity, involuntary movements) and changes in mental status.', 'Metabolism', 'Amisulpride, Pimavanserin', 'Oxitriptan, Viloxazine, Vortioxetine, Tryptophan, Vilazodone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74068/', '[1] Ozdemir V, Naranjo CA, Herrmann N, Reed K, Sellers EM, Kalow W "Paroxetine potentiates the central nervous system side effects of perphenazine: contribution of cytochrome P4502D6 inhibition in vivo." Clin Pharmacol Ther 62 (1997): 334-47[2] "Product Information. Paxil (paroxetine)." GlaxoSmithKline, Research Triangle Park, NC.[3] Nicholson SD "Extra pyramidal side effects associated with paroxetine." West Engl Med J 107 (1992): 90-1[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interaction