INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367828, 'Dextromethorphan', 'Thioridazine', 'Moderate', 'Coadministration with CYP450 2D6 inhibitors may increase the plasma concentrations of dextromethorphan in patients who are extensive metabolizers of this isoenzyme (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent). The proposed mechanism is inhibition of the CYP450 2D6-mediated O-demethylation of dextromethorphan. Increased plasma concentrations increase the risk of dextromethorphan-related adverse effects (e.g., agitation, confusion, tremor, insomnia, diarrhea, and respiratory depression) and serotonin syndrome.', 'DDInter', 1767317475, 'Caution should be exercised if these drugs must be used together. Patients should be monitored for signs of dextromethorphan adverse effects (e.g., agitation, confusion, tremor, insomnia, diarrhea, and respiratory depression) and serotonin syndrome, and advised to notify their health care professional if these adverse effects develop or worsen. Dose reduction of dextromethorphan may also be required.', 'Metabolism', 'Caution should be exercised if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
