INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1367846, 'Dextromethorphan', 'Valdecoxib', 'Minor', 'According to the manufacturer, the coadministration with valdecoxib 40 mg twice a day for 7 days has been shown to significantly increase the plasma concentrations of dextromethorphan. The proposed mechanism is decreased clearance of dextromethorphan due to inhibition of the CYP450 2D6 enzymatic pathway by valdecoxib at high dosages. No particular intervention should be necessary when valdecoxib is administered under normal circumstances with dextromethorphan. At unusually high dosages of valdecoxib, there may be a theoretical risk of increased adverse effects of dextromethorphan such as drowsiness, dizziness, excitation, mental confusion, and respiratory depression. Parecoxib, a prodrug of valdecoxib, may also interact.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
