INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1368003, 'Biperiden', 'Molindone', 'Moderate', 'Centrally-acting anticholinergic agents may antagonize the therapeutic effects of neuroleptic agents. Although these drugs have been used together clinically, the possibility of increased risk of adverse effects such as central nervous system depression and tardive dyskinesia should also be considered. In addition, excessive anticholinergic effects may occur in combination use, which can result in paralytic ileus, hyperthermia, heat stroke, and the anticholinergic intoxication syndrome.', 'DDInter', 1767317475, 'Caution is advised if anticholinergic agents are used with neuroleptic agents, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Prophylactic administration of anticholinergic agents is sometimes given clinically during neuroleptic therapy for drug-induced parkinsonism or extrapyramidal symptoms but may not always be appropriate.', 'Antagonism', 'Caution is advised if anticholinergic agents are used with neuroleptic agents, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1368004, 'Biperiden', 'Morphine (liposomal)', 'Moderate', 'Central anticholinergic agents may have additive central nervous system (CNS) effects with cannabinoids, barbiturates, opiates, and alcohol. These agents individually can cause cognitive and psychomotor impairment, drowsiness, and dizziness, thus concomitant use may result in more potent effects. In addition, the potential for abuse may be increased when central anticholinergic agents are combined with these drugs.', 'DDInter', 1767317475, 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness.', 'Synergism', 'Patients taking central anticholinergic agents in combination with other CNS depressants should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
