INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1368049, 'Biperiden', 'Pramlintide', 'Moderate', 'The use of pramlintide in combination with drugs that alter gastrointestinal motility (e.g., loperamide; anticholinergic agents) or drugs that slow the intestinal absorption of nutrients (e.g., alpha-glucosidase inhibitors) has not been studied.', 'DDInter', 1767317475, 'Due to its effect on gastric emptying, pramlintide therapy should not be considered for patients taking drugs that alter gastrointestinal motility or drugs that slow the intestinal absorption of nutrients.', 'Absorption', 'Due to its effect on gastric emptying, pramlintide therapy should not be considered for patients taking drugs that alter gastrointestinal motility or drugs that slow the intestinal absorption of nutrients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
