INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148841, 'Teriflunomide', 'Oxaliplatin', 'Major', 'The use of leflunomide with other immunosuppressive or myelosuppressive agents may increase the risk of infections. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. The recent, concomitant, or sequential use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', NULL, 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa.', NULL, 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa. Due to the prolonged elimination half-life of leflunomide''s active metabolite, an interaction may occur even when these agents are initiated after the discontinuation of treatment with leflunomide or teriflunomide. Liver enzymes, bilirubin, platelet, white blood cell count, and hemoglobin or hematocrit should be evaluated at baseline and regularly during therapy.', 'Synergism', 'Pertuzumab, Vismodegib, Belzutifan, Sonidegib, Mitotane, Porfimer sodium, Estramustine, Aminolevulinic acid, Tazemetostat, Vorinostat, Arsenic trioxide, More', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149004/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767369485);
