INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (74216, 'Erdafitinib', 'Conivaptan', 'Major', 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 may increase the plasma concentrations and risk of toxicity of erdafitinib, which is a substrate of these isoenzymes.', NULL, 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 should generally be avoided.', NULL, 'Coadministration of erdafitinib with potent inhibitors of CYP450 2C9 or 3A4 should generally be avoided. If concomitant use is unavoidable, patients should be closely monitored for the development of adverse effects. Dosage adjustments as well as clinical and laboratory monitoring of erdafitinib should be considered whenever a CYP450 2C9 or 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', 'Alpelisib, Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74303/', '[1] "Product Information. Balversa (erdafitinib)." Janssen Products, LP, Horsham, PA.', 1767369485);
