INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (74284, 'Conivaptan', 'Lurasidone', 'Major', 'Coadministration with conivaptan may significantly increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Conivaptan is a mechanism-based inhibitor of CYP450 3A4.', NULL, 'Concomitant use of conivaptan with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index should generally be avoided.', NULL, 'Concomitant use of conivaptan with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index should generally be avoided. Subsequent treatment with CYP450 3A4 substrates may be initiated no sooner than 1 week after completion of conivaptan treatment.', 'Metabolism', 'Fluphenazine, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74371/', '[1] "Product Information. Vaprisol (conivaptan)." Cumberland Pharmaceuticals Inc, Nashville, TN.[2] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
