INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (74338, 'Conivaptan', 'Pralsetinib', 'Major', 'Coadministration with potent CYP450 3A inhibitors may significantly increase the plasma concentrations of pralsetinib, which is primarily metabolized by the isoenzyme. Increased exposure to pralsetinib may increase the risk of serious adverse effects such as interstitial lung disease/pneumonitis, liver transaminase elevations, hypertension, and hemorrhage.', NULL, 'Concomitant use of pralsetinib with potent CYP450 3A inhibitors should generally be avoided.', NULL, 'Concomitant use of pralsetinib with potent CYP450 3A inhibitors should generally be avoided.', 'Metabolism', 'Alpelisib, Lenvatinib, Trametinib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74425/', '[1] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation, Cambridge, MA.[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation, Cambridge, MA.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
