INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (74370, 'Conivaptan', 'Saxagliptin', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly alter the plasma concentrations of saxagliptin and its pharmacologically active metabolite, both of which are substrates of the isoenzyme.', NULL, 'Some authorities advise against using saxagliptin in combination with potent CYP450 3A4 inhibitors.', NULL, 'Some authorities advise against using saxagliptin in combination with potent CYP450 3A4 inhibitors. Other authorities recommend that the dosage of saxagliptin should be limited to 2.5 mg once daily when used with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Metabolism', 'Tolvaptan', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74457/', '[1] "Product Information. Onglyza (saxagliptin)." Bristol-Myers Squibb, Princeton, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[4] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[5] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[6] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[7] "Position Statement: evid