INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (74385, 'Conivaptan', 'Stiripentol', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways. Conversely, many of these inhibitors are also substrates of CYP450 1A2 and/or 3A4, and coadministration with stiripentol may alter their plasma concentrations as well. Stiripentol is both an inhibitor and inducer of CYP450 1A2 and 3A4 in vitro.', NULL, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways.', NULL, 'Caution is advised when stiripentol is used with inhibitors of CYP450 1A2 and/or 3A4 that are also substrates of these enzymatic pathways. Patients should be monitored for potentially increased adverse effects of stiripentol such as anorexia, nausea, vomiting, weight loss, somnolence, dizziness, confusion, difficulty concentrating, ataxia, hypotonia, tremor, hyperkinesia, dysarthria, suicidal ideation/behavior, neutropenia, and thrombocytopenia. In addition, clinical and laboratory monitoring may be appropriate for concomitant drugs whenever stiripentol is added to or withdrawn from therapy. Dosage adjustments or alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Phenytoin, Levetiracetam, Oxcarbazepine, Ethotoin, Rufinamide, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74472/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[6] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (74386, 'Conivaptan', 'Sufentanil', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of sufentanil, which is primarily metabolized by the isoenzyme. Increased sufentanil concentrations may enhance or prolong pharmacologic effects and potentiate the risk of central nervous system and respiratory depression.', NULL, 'Patients receiving sufentanil with potent CYP450 3A4 inhibitors should be carefully monitored for excessive central nervous system and respiratory depression, and dosage adjustments made accordingly if necessary.', NULL, 'Patients receiving sufentanil with potent CYP450 3A4 inhibitors should be carefully monitored for excessive central nervous system and respiratory depression, and dosage adjustments made accordingly if necessary.', 'Metabolism', 'Thiopental, Remifentanil, Sodium oxybate, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Propofol, Enflurane, Esketamine, Sevoflurane, More', 'Tolvaptan', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/74473/', '[1] Bartkowski RR, Goldberg ME, Huffnagle S, Epstein RH "Sufentanil disposition. Is it affected by erythromycin administration?" Anesthesiology 78 (1993): 260-5[2] "Product Information. Sufenta (sufentanil)." Janssen Pharmaceutica, Titusville, NJ.[3] Tateishi T, Krivoruk Y, Ueng YF, Wood AJ, Guengerich FP, Wood M "Identification of human cytochrome P-450 3A4 as the enzyme responsible for fentanyl and sufentanil N-dealkylation." Anesth Analg 82 (1996): 167-72[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous entera