INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149276, 'Esketamine', 'Oxymetazoline (nasal)', 'Moderate', 'INTERVAL: Data are limited with respect to any potential interaction between intranasal esketamine and other nasal products during concomitant administration. In pharmacokinetic studies, mometasone furoate and oxymetazoline hydrochloride administered intranasally one hour before esketamine demonstrated no clinically significant effects on esketamine peak plasma concentration and exposure.', NULL, 'To minimize the risk of interaction, patients who require a nasal corticosteroid or nasal decongestant on a day when esketamine dosing is scheduled should administer these medications at least one hour before esketamine.', NULL, 'To minimize the risk of interaction, patients who require a nasal corticosteroid or nasal decongestant on a day when esketamine dosing is scheduled should administer these medications at least one hour before esketamine.', 'Others', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Vortioxetine, Doxepin, Viloxazine, Amoxapine, Imipramine, Fluvoxamine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149439/', '[1] "Product Information. Spravato (esketamine)." Janssen Pharmaceuticals, Titusville, NJ.', 1767369485);
